import React, { Component } from "react";

import { FaFilter } from "react-icons/fa";
import { ImSortAmountDesc } from "react-icons/im";

export default class SearchBar extends Component {
  render() {
    return (
      <div className="flex items-center justify-center border-b border-gray-300 pr-2">
        <input
          type="text"
          placeholder="SEARCH..."
          onFocus={(e) => (e.target.placeholder = "")}
          onBlur={(e) => (e.target.placeholder = "SEARCH...")}
          onChange={this.handleType}
          className="w-9/11 text-xl p-4 text-gray-700 focus:outline-none focus:border focus:border-solid focus:border-blue-500"
        />
        <button className="h-full w-1/11 flex items-center justify-center bg-green-200">
          <FaFilter className="text-xl" />
        </button>
        <button className="h-full w-1/11 flex items-center justify-center bg-green-300 ">
          <ImSortAmountDesc className="text-xl" />
        </button>
      </div>
    );
  }
}
