import React, { Component } from "react";
import { connect } from "react-redux";
import { saveSections } from "../../../state/actions";

import CartItem from "./CartItem";

const mapStateToProps = (state) => {
  return {
    activeSections: state.activeSections,
    activeCalendar: state.activeCalendar,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    saveSections: (sections) => dispatch(saveSections(sections)),
  };
};

class Cart extends Component {
  constructor(props) {
    super(props);

    this.updateCart = this.updateCart.bind(this);
  }

  updateCart(thisTitle, val) {
    let currentSections = this.props.activeSections;
    for (let i = 0; i < currentSections.length; i++) {
      if (currentSections[i].title === thisTitle) {
        currentSections.splice(i, 1);
      }
    }

    this.props.saveSections(currentSections);
  }

  render() {
    console.log(this.props.activeCalendar);
    return (
      <div className="w-1/5 h-full bg-blue-100 flex flex-col">
        {this.props.activeCalendar.sections &&
          this.props.activeCalendar.sections.map((item, key) => {
            return (
              <CartItem item={item} key={key} updateCart={this.updateCart} />
            );
          })}
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
