import React, { Component } from "react";
import { connect } from "react-redux";
import ClassItem from "../../components/Planner/ClassList/ClassItem";
import { filterClasses } from "../../state/actions";
import { filter } from "./Methods";

const mapStateToProps = (state) => {
  return {
    displayedClasses: state.displayedClasses,
    classes: state.classes,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    filterClasses: (classes) => dispatch(filterClasses(classes)),
  };
};

class ClassList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hasTyped: false,
      typedText: "",
    };

    this.handleType = this.handleType.bind(this);
  }

  handleType(e) {
    // Is used to determine if we show "not found" or "start typing"
    this.setState({ typedText: e.target.value });

    let numWords = e.target.value.split(" ").length;
    let val = e.target.value.toLowerCase().split(" ");
    let currentClasses = filter(numWords, this.props.classes, val);

    this.props.filterClasses(currentClasses);
  }

  render() {
    let classList = this.props.displayedClasses.slice(0, 10);

    return (
      <div className="w-1/5 h-full bg-white shadow-xl flex flex-col">
        <input
          type="text"
          placeholder="SEARCH..."
          onFocus={(e) => (e.target.placeholder = "")}
          onBlur={(e) => (e.target.placeholder = "Search classes...")}
          onChange={this.handleType}
          className="w-full text-xl p-4 border-b border-gray-300 text-gray-700 focus:outline-none focus:border focus:border-solid focus:border-blue-500"
        />
        <div className="overflow-y-auto h-full">
          {classList.map((item, key) => {
            return <ClassItem item={item} key={key} />;
          })}
          {classList.length == 0 && this.state.typedText.length > 0 && (
            <div className="flex-col justify-end uppercase">
              <div>
                <div>
                  <h2>no results found!!!</h2>
                  <p>nothing matched your search terms</p>
                </div>
                <div>
                  <p>
                    tip: use filters if you are looking for something specific
                  </p>
                </div>
              </div>
            </div>
          )}
          {classList.length == 0 && this.state.typedText.length < 1 && (
            <div className="flex-col justify-end uppercase h-full text-center">
              <div className="h-1/3 flex flex-col justify-between">
                <div>
                  <h2 className="font-black text-xl">no results found!</h2>
                  <p className="text-md">nothing matched your search terms</p>
                </div>
                <div>
                  <p className="text-xs">
                    tip: use filters if you are looking for something specific
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ClassList);
