import React, { Component } from "react";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default class PlannerNav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menuOpen: false,
    };
  }
  render() {
    return (
      <div className="w-full py-4 flex justify-center items-center bg-white shadow-xl">
        <div className="w-full px-8 flex justify-between items-center">
          <div className="font-bold text-xl">
            <a href="/">BUNexus</a>
          </div>
          <div className="flex items-center">
            <Link to="/planner" className="text-blue-500 hover:underline mx-2">
              Planner
            </Link>
            <Link
              to="/planner/about"
              className="text-blue-500 hover:underline mx-2"
            >
              About
            </Link>
            <div>
              <div
                className="w-10 h-10 bg-green-500 rounded-full ml-5"
                onMouseEnter={() => this.setState({ menuOpen: true })}
                onMouseLeave={() => this.setState({ menuOpen: false })}
              ></div>
              {this.state.menuOpen && (
                <div className="absolute">
                  <Link
                    to="/planner/about"
                    className="text-blue-500 hover:underline mx-2"
                  >
                    Login
                  </Link>
                  <Link
                    to="/planner/about"
                    className="text-blue-500 hover:underline mx-2"
                  >
                    Profile
                  </Link>
                  <Link
                    to="/planner/about"
                    className="text-blue-500 hover:underline mx-2"
                  >
                    Settings
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
