import React, { Component } from "react";

export default class SearchBar extends Component {
  render() {
    return (
      <input
        type="text"
        placeholder="SEARCH..."
        onFocus={(e) => (e.target.placeholder = "")}
        onBlur={(e) => (e.target.placeholder = "SEARCH...")}
        onChange={this.handleType}
        className="w-full text-xl p-4 border-b border-gray-300 text-gray-700 focus:outline-none focus:border focus:border-solid focus:border-blue-500"
      />
    );
  }
}
