import React, { Component } from "react";
import { connect } from "react-redux";
import { changeCalendar } from "../../../state/actions";

import CalendarGraph from "./CalendarGraph/CalendarGraph";
import CalendarStats from "./CalendarStats";

const testStats = {
  quality: 3,
  instructor: 4,
  difficulty: 2,
  workload: 4.5,
  majorClasses: 2,
  hubClasses: 2,
  newHubs: 3,
  hoursDay: 1.7,
  hoursWeek: 8.5,
  earliest: "8 AM",
  latest: "4 PM",
};

const mapStateToProps = (state) => {
  return {
    activeCalendar: state.activeCalendar,
    calendars: state.calendars,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    changeCalendar: (calendar) => dispatch(changeCalendar(calendar)),
  };
};

class Calendar extends Component {
  render() {
    return (
      <>
        {this.props.activeCalendar && (
          <div className="w-4/5 h-full bg-white p-2 px-4 flex flex-col items-center justify-between">
            <div className="flex w-full items-center justify-start">
              <h1 className="font-black text-lg xl:text-xl uppercase text-left">
                Schedules
              </h1>
              <select className="capitalize ml-4">
                {this.props.calendars.map((item, key) => {
                  let selected = this.props.activeCalendar == item;
                  return (
                    <option key={key} className="capitalize" onClick={() => this.props.changeCalendar}>
                      {item.title}
                    </option>
                  );
                })}
              </select>
            </div>
            <CalendarGraph />
            <CalendarStats stats={testStats} />
          </div>
        )}
      </>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Calendar);
