import React, { Component } from "react";
import { IoIosArrowDown, IoIosArrowUp } from "react-icons/io";

export default class Recommended extends Component {
  render() {
    let isOpen = this.props.recommendedOpen;

    return (
      <div
        className={
          "bg-white shadow-xl flex flex-col w-full overflow-hidden mt-4 " +
          (isOpen ? "h-1/4" : "h-1/12")
        }
      >
        <div className="bg-white p-5 flex uppercase font-bold flex justify-between items-center">
          <h2 className="font-black text-xl">recommended</h2>
          <button className="bg-black text-white px-3 rounded-full">
            {isOpen ? (
              <div className="flex items-center justify-center">
                hide <IoIosArrowDown />
              </div>
            ) : (
              <div className="flex items-center justify-center">
                show <IoIosArrowUp />
              </div>
            )}
          </button>
        </div>
        {isOpen && (
          <div className="bg-white p-5 flex uppercase font-bold text-gray-500">
            <p className="w-3/5">course</p>
            <p className="w-1/5 text-center">qual</p>
            <p className="w-1/5 text-center">diff</p>
          </div>
        )}
      </div>
    );
  }
}
