import React, { Component } from "react";
import { connect } from "react-redux";
import { changeCalendar } from "../../../state/actions";

const mapStateToProps = (state) => {
  return {
    activeCalendar: state.activeCalendar,
    calendars: state.calendars,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    changeCalendar: (calendar) => dispatch(changeCalendar(calendar)),
  };
};

class ScheduleList extends Component {
  constructor(props) {
    super(props);

    this.changeCalendar = this.changeCalendar.bind(this);
  }

  changeCalendar(e) {
    let itemTitle = e.target.value;

    for (const schedule of this.props.calendars)
      if (schedule.title === itemTitle) this.props.changeCalendar(schedule);
  }

  render() {
    return (
      <select className="capitalize" onChange={this.changeCalendar}>
        {this.props.calendars.map((item, key) => {
          return (
            <option className="capitalize" key={key} value={item.title}>
              {item.title}
            </option>
          );
        })}
      </select>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ScheduleList);
