import React from "react";

import { FaUserAlt } from "react-icons/fa";
import { IoLocationSharp } from "react-icons/io5";

const colors = ["green", "yellow", "red", "blue", "indigo", "purple", "pink"];

function textToTime(txt) {
  let hour = parseInt(txt.substring(0, 2));
  let min = parseInt(txt.substring(3, 5));

  let time = hour * 60 + min;
  let diff = time - 7 * 60;

  return diff;
}

function textToDiff(txt1, txt2) {
  let hour = parseInt(txt.substring(0, 2));
  let min = parseInt(txt.substring(3, 5));

  let time = hour * 60 + min;
  let diff = time - 7 * 60;

  return diff;
}

export default function CalendarItem(props) {
  let color = colors[props.section.id];
  let fromTop = textToTime(props.section.start);
  let calcHeight = textToDiff(props.section.start, props.section.end);

  return (
    <div
      className={
        "absolute bg-" +
        color +
        "-100 border-l-4 border-" +
        color +
        "-500 text-" +
        color +
        "-500 p-4 w-full flex flex-col items-left justify-center"
      }
      style={{
        top: fromTop,
        height: calcHeight,
      }}
    >
      <h3 className="font-bold text-lg">{props.section.title}</h3>
      <h4 className="flex items-center">
        <FaUserAlt />
        <span className="ml-2">{props.section.professor}</span>
      </h4>
      <h4 className="flex items-center">
        <IoLocationSharp />
        <span className="ml-2">{props.section.location}</span>
      </h4>
    </div>
  );
}
