import React from "react";
import { Component } from "react";

function getDayInits(arr) {
  let str = "";
  for (const day of arr) str += day.charAt(0);

  return str.toUpperCase();
}

export default class CartItem extends Component {
  render() {
    return (
      <div
        className="w-full h-26 flex items-center p-3 border-b border-solid border-gray-400"
        onClick={() => this.props.updateCart(this.props.item.title, this.props.item)}
      >
        <input type="checkbox" className="w-4 h-4 mr-2" defaultChecked={true} />
        <div className="flex flex-col justify-center">
          {this.props.item.title}
          <div className="flex items-center">
            <span className="font-bold mr-2">
              {getDayInits(this.props.item.days)}
            </span>
            <span>
              {this.props.item.start}-{this.props.item.end}
            </span>
          </div>
        </div>
      </div>
    );
  }
}
