import React, { Component } from "react";
import { connect } from "react-redux";

import { IoIosArrowDown } from "react-icons/io";
import { MdModeEdit } from "react-icons/md";
import { FaTrashAlt, FaCopy } from "react-icons/fa";

import { changeCalendar } from "../../../state/actions";

const mapStateToProps = (state) => {
  return {
    activeCalendar: state.activeCalendar,
    calendars: state.calendars,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    changeCalendar: (calendar) => dispatch(changeCalendar(calendar)),
  };
};

class ScheduleList extends Component {
  constructor(props) {
    super(props);

    this.changeCalendar = this.changeCalendar.bind(this);
  }

  changeCalendar(e) {
    let itemTitle = e.target.value;

    for (const schedule of this.props.calendars)
      if (schedule.title.toLowerCase() === itemTitle.toLowerCase())
        this.props.changeCalendar(schedule);
  }

  render() {
    return (
      <div className="ml-3 relative bg-gray-200 rounded-full px-3 flex items-center capitalize cursor-pointer">
        {this.props.activeCalendar.title}
        <IoIosArrowDown />
        <div className="absolute top-full mt-3 left-0 shadow-2xl rounded-lg flex flex-col items-center justify-between w-52 overflow-hidden">
          <span>
            {this.props.calendars.map((item, key) => {
              if (item.title !== this.props.activeCalendar.title)
                return (
                  <div
                    className="p-4 hover:bg-blue-100"
                    key={key}
                    onClick={() => this.props.changeCalendar(item)}
                  >
                    {item.title}
                  </div>
                );
            })}
          </span>
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ScheduleList);
