import React, { Component } from "react";

import CalendarItem from "./CalendarItem";

const daysOfWeek = ["monday", "tuesday", "wednesday", "thursday", "friday"];

const hours = [7, 19];

export default class CalendarGraph extends Component {
  constructor(props) {
    super(props);

    this.hours = this.hours.bind(this);
  }

  hours() {
    let arr = [];
    for (let i = hours[0]; i <= hours[1]; i++) {
      arr.push(<div>{i}</div>);
    }

    return arr;
  }

  days() {
    let arr = [];
    for (const i of daysOfWeek) {
      arr.push(<div className="flex flex-col">{this.hours}</div>);
    }

    return arr;
  }

  render() {
    return (
      <div>
        <div className="flex">{this.days}</div>
      </div>
    );
  }
}
