import React, { Component } from "react";

import CalendarItem from "./CalendarItem";

const daysOfWeek = ["monday", "tuesday", "wednesday", "thursday", "friday"];

const hours = [7, 19];

export default class CalendarGraph extends Component {
  constructor(props) {
    super(props);

    this.hours = this.hours.bind(this);
    this.days = this.days.bind(this);
  }

  hours() {
    let arr = [];
    for (let i = hours[0]; i <= hours[1]; i++) {
      arr.push(
        <div
          className={
            "flex-grow " + (i == hours[1] ? "" : "border-b border-gray-300")
          }
        ></div>
      );
    }

    return arr;
  }

  days() {
    let arr = [];
    for (const i of daysOfWeek) {
      arr.push(
        <div className="flex flex-col w-full">
          <div className="uppercase text-center text-gray-600 mb-4">{i}</div>
          <div
            className={
              "flex flex-col flex-grow " +
              (i == daysOfWeek[0]
                ? "border border-gray-300"
                : "border border-l-0 border-gray-300")
            }
          >
            {this.hours()}
          </div>
        </div>
      );
    }

    return arr;
  }

  hourLeyend() {
    let arr = [];
    for (let i = hours[0]; i <= hours[1]; i++) {
      arr.push(<div>{i}:00</div>);
    }
  }

  render() {
    return (
      <div className="flex h-2/3">
        <div className="flex w-full h-full overflow-hidden">
          <div>{this.hourLeyend()}</div>
          {this.days()}
        </div>
      </div>
    );
  }
}
