import React, { Component } from "react";

import CalendarItem from "./CalendarItem";

const daysOfWeek = ["monday", "tuesday", "wednesday", "thursday", "friday"];

const hours = [7, 19];

export default class CalendarGraph extends Component {
  constructor(props) {
    super(props);

    this.hours = this.hours.bind(this);
    this.days = this.days.bind(this);
  }

  hours() {
    let arr = [];
    for (let i = hours[0]; i <= hours[1]; i++) {
      arr.push(
        <div
          className={
            "flex-grow border-b " + (i == hours[1] ? "" : "border-gray-200")
          }
        >
          {i}
        </div>
      );
    }

    return arr;
  }

  days() {
    let arr = [];
    for (const i of daysOfWeek) {
      arr.push(
        <div className="flex flex-col flex-grow border-r border-gray-200">
          {this.hours()}
        </div>
      );
    }

    return arr;
  }

  render() {
    return (
      <div className="flex">
        <div className="flex w-full h-full overflow-hidden border border-gray-200">
          {this.days()}
        </div>
      </div>
    );
  }
}
