import React from "react";

function getDayInits(arr) {
  let str = "";
  for (const day of arr) str += day.charAt(0);

  return str.toUpperCase();
}

export default function CartItem(props) {
  return (
    <div className="w-full h-26 flex items-center p-3 border-b border-solid border-gray-400">
      <input
        type="checkbox"
        className="w-4 h-4 mr-2"
        defaultChecked={true}
        onChange={(e) => {
          this.setState({ checked: e.target.checked });
          props.updateCart(props.item.title, props.item, e.target.checked);
        }}
        checked={this.state.checked}
      />
      <div className="flex flex-col justify-center">
        {props.item.title}
        <div className="flex items-center">
          <span className="font-bold mr-2">{getDayInits(props.item.days)}</span>
          <span>
            {props.item.start}-{props.item.end}
          </span>
        </div>
      </div>
    </div>
  );
}
