import React from "react";

import { FaUserAlt } from "react-icons/fa";
import { IoLocationSharp, IoClose } from "react-icons/io5";
import { textToDiff, textToTime } from "./CalendarMethods";

const colors = ["green", "yellow", "red", "blue", "indigo", "purple", "pink"];

export default function CalendarItem(props) {
  let color = colors[props.section.id];
  let fromTop = props.h + textToTime(props.section.start, 7) * props.h;
  let calcHeight = textToDiff(props.section.start, props.section.end) * props.h;

  console.log(props.h);

  return (
    <div
      className={
        "absolute bg-" +
        color +
        "-100 border-l-4 border-" +
        color +
        "-500 text-" +
        color +
        "-500 px-2 w-full flex flex-col items-left justify-center overflow-hidden"
      }
      style={{
        top: fromTop,
        height: calcHeight,
      }}
    >
      <div
        className="flex justify-between items-end"
        style={{ height: calcHeight / 2 }}
      >
        <h3 className="font-bold text-2xs 2xl:text-xs">
          {props.section.title}
        </h3>
        <IoClose />
      </div>
      <h4
        className="flex items-center text-3xs uppercase"
        style={{ height: calcHeight / 4 }}
      >
        <FaUserAlt />
        <span className="ml-2">{props.section.professor}</span>
      </h4>
      <h4
        className="flex items-center text-3xs uppercase"
        style={{ height: calcHeight / 4 }}
      >
        <IoLocationSharp />
        <span className="ml-2">{props.section.location}</span>
      </h4>
    </div>
  );
}
