import React from "react";

function getDayInits(arr) {
  let str = "";
  for (const day of arr) str += day.charAt(0);

  return str.toUpperCase();
}

export default function CartItem(props) {
  return (
    <div className="my-3 w-full h-26 flex items-center">
      <input type="checkbox" className="w-5 h-5" />
      <div className="flex flex-col justify-center">
        {props.item.title}
        <div className="flex items-center">
          <span className="font-bold mr-2">{getDayInits(props.item.days)}</span>
          <span>
            {props.item.start}-{props.item.end}
          </span>
        </div>
      </div>
    </div>
  );
}
