import React from "react";

import { FaUserAlt } from "react-icons/fa";
import { IoLocationSharp, IoClose } from "react-icons/io5";
import { textToDiff } from "./CalendarMethods";

const colors = ["green", "yellow", "red", "blue", "indigo", "purple", "pink"];

export default function CalendarItem(props) {
  let color = colors[props.section.id];
  let fromTop = 32 + (textToTime(props.section.start) * props.h) / 60;
  let calcHeight =
    (textToDiff(props.section.start, props.section.end) * props.h) / 60;

  return (
    <div
      className={
        "absolute bg-" +
        color +
        "-100 border-l-4 border-" +
        color +
        "-500 text-" +
        color +
        "-500 px-2 w-full flex flex-col items-left justify-center"
      }
      style={{
        top: fromTop,
        height: calcHeight,
      }}
    >
      <div className="flex justify-between">
        <h3 className="font-bold text-2xs">{props.section.title}</h3>
        <IoClose />
      </div>
      <h4 className="flex items-center text-3xs uppercase">
        <FaUserAlt />
        <span className="ml-2">{props.section.professor}</span>
      </h4>
      <h4 className="flex items-center text-3xs uppercase">
        <IoLocationSharp />
        <span className="ml-2">{props.section.location}</span>
      </h4>
    </div>
  );
}
