import React, { Component } from "react";
import { connect } from "react-redux";

import CalendarGraph from "./CalendarGraph/CalendarGraph";
import CalendarStats from "./CalendarStats";

const testStats = {
  quality: 3,
  instructor: 4,
  difficulty: 2,
  workload: 4.5,
  majorClasses: 2,
  hubClasses: 2,
  newHubs: 3,
  hoursDay: 1.7,
  hoursWeek: 8.5,
  earliest: "8 AM",
  latest: "4 PM",
};

const mapStateToProps = (state) => {
  return {
    activeCalendar: state.activeCalendar,
    calendars: state.calendars,
  };
};

class Calendar extends Component {
  render() {
    return (
      <>
        {this.props.activeCalendar && (
          <div className="w-4/5 h-full bg-white p-2 px-4 flex flex-col items-center justify-between">
            <div>
              <h1 className="font-black text-lg xl:text-xl uppercase w-full text-left">
                {this.props.activeCalendar.title}
              </h1>
              <select>
                {this.props.calendars.map((item, key) => {
                  let selected = this.props.activeCalendar == item;
                  return (
                    <option
                      key={key}
                      changeSelected={this.changeSchedule}
                    >
                      {item.title}
                    </option>
                  );
                })}
              </select>
            </div>
            <CalendarGraph />
            <CalendarStats stats={testStats} />
          </div>
        )}
      </>
    );
  }
}

export default connect(mapStateToProps)(Calendar);
